const btnMenuOpen=document.querySelector(".burger--open"),btnMenuClose=document.querySelector(".burger--close"),menu=document.querySelector(".menu");btnMenuOpen.addEventListener("click",function(e){e.preventDefault(),menu.classList.add("menu--open")}),btnMenuClose.addEventListener("click",function(e){e.preventDefault(),menu.classList.remove("menu--open")});const form=document.querySelector("form"),submitBtn=form.querySelector(".btn"),inputName=form.querySelector("#name"),inputEmail=form.querySelector("#email"),textareaMessage=form.querySelector("#message"),countLength=(e,t,n)=>{e.addEventListener("input",()=>{e.value.length>=t?n.disabled=!1:n.disabled=!0})};countLength(inputName,2,inputEmail),countLength(inputEmail,5,textareaMessage),countLength(textareaMessage,10,submitBtn);